// JSON Object list
// Component -> ListView contains List items
// FlatList will genearate list

const gallary = [
  {
    name: "img1",
    timeStamp: "2 July 2022 7:16pm",
    extension: "png",
    size: "274.26KB",
    height: 1411,
    width: 720,
    folder: "Screenshots"
  },
  {
    name: "video1",
    timeStamp: "2 July 2022 7:18pm",
    extension: "mp4",
    size: "274.26KB",
    height: 1411,
    width: 720,
    folder: "Video"
  },
  {
    name: "img2",
    timeStamp: "2 July 2022 7:20pm",
    extension: "jpg",
    size: "274.26KB",
    height: 1411,
    width: 720,
    folder: "Camera"
  }
]

const Item = (itemData: any) => (
  <View>
    <Text>{itemData['name']}</Text>
  </View>
);

const renderItem = ({item}: any) => Item(item);

export default function App() {
  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      {/* <Text style={styles.textStyle}>Welcome</Text> */}
      {/* renderItem is an algo applied on array */}
      <FlatList data={gallary} renderItem={renderItem}></FlatList>  
      </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  textStyle: {
    color: "red",
    margin: 20,
    fontSize: 25,
  },

  background: {
    backgroundColor: '#fae',
