import { StatusBar } from "expo-status-bar";
import { Component, ReactNode, useEffect, useState } from "react";
import { Button, FlatList, StyleSheet, Text, TouchableHighlight, View, Image, Platform, ActivityIndicator } from 'react-native';

export default function App() {

  const url = "https://maps.googleapis.com/maps/api/place/nearbysearch/json?keyword=cruise&location=-33.8670522%2C151.1957362&radius=1500&type=restaurant&key=AIzaSyCPvqQRjVbo0OA1QFbijKILLJAtJRXPsok"
  const [showIndicator, setIndicator] = useState(true);

  const getNearByPlacesInfoFromAPI = async () => {
    try {
      const responseFromAPI = fetch(url).then((response) => response.json());
      console.log("responseFromAPI is: ");
      console.log(responseFromAPI);
      setIndicator(false);
    }
    catch(error) {
      console.error("Something Went Wront: " + error);
    }
  }

  useEffect(() => {getNearByPlacesInfoFromAPI()}); // Jaise hi screen khule to BG me fetching start

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      <View style={styles.header}><Text style={styles.headerText}>Nearby Places</Text></View>
      {/* <Text style={styles.textStyle}>Welcome</Text> */}
      {/* renderItem is an algo applied on array */}
      {/* {showIndicator ? <ActivityIndicator style={styles.activityIndicator} /> : <FlatList data={news} renderItem={renderItem}></FlatList>} */}
      <Text>Welcome to Nearby Place Search app</Text>
      </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    // padding: 10
    // margin: 10
  },

  background: {
    backgroundColor: '#fae',
    fontSize: 24,
    marginBottom: 20,
  },

  item: {
    backgroundColor: '#DCDBDB2B',
    padding: 10,
    margin: 10,
    width: Platform.OS === "android" ? "95%": "60%",
    alignSelf: 'center',
    elevation: 1,
    borderRadius: 10,
    shadowColor: '#6E69692C',
    shadowOffset: {height: 6, width: 4},
  },

  title: {
    fontSize: 15,
    color: '#',
  },

  subTitle: {
    fontSize: 12,
    color: '#7C7373',
  },

  circle: {
    height: 17,
    width: 17,
    borderRadius: 20,
    backgroundColor: "#8E8686",
    marginRight: 10
  },

  circleTitle: {
    flex: 1,
    flexDirection: "row",
  },

  button: {
    borderRadius: 10,
    height: 30,
    width: 100,
    backgroundColor: "#6E696943",
    textAlign: "center",
    justifyContent: 'center',
  },

  image: {
    height: 400,
    width: "100%",
    borderRadius: 10,
  },

  header: {
    height: 80,
    width: "100%",
    backgroundColor: "#D7A6E6",
    justifyContent: "center"
  },

  headerText: {
    alignSelf: "center",
    fontSize: 30,
    fontWeight: "bold",
  }, 

  activityIndicator: {
    justifyContent: "center",
  },
});



